import { CircularProgress } from "@mui/material";
import { useEffect, useState } from "react";
import { User } from "../api/type";
import { getUser, updateUserProfile } from "../api/user";
import { useApp } from "../App";
import { Avatar } from "./Avatar";
import { Button } from "./Button";
import { TextInput } from "./TextInput";

interface Props { }
// const url = 'data:image/jpeg;base64,'

export const ProfileCard = ({ }: Props) => {
  const [isEditing, setIsEditing] = useState(false);
  const { user, setUser } = useApp(); //useApp 数据中心老板写的函数，
  const [inputEmail, setInputEmail] = useState("");
  const [inputName, setInputName] = useState("");
  const [uploading, setUploading] = useState<boolean>(false);

  return (
    // w-[690px] h-[100px]
    <div className="divide-y divide-gray-300 w-full h-full">
      <div className="flex flex-row flex-basis auto ml-10">
        <div className="transition-all mt-6 w-14 h-14 relative hover:scale-95">
          <input
            type={"file"}
            className="opacity-0 absolute inset-0 cursor-pointer"
            onChange={(e) => {
              const file = e.target.files?.[0];
              if (file && user?.uid) {
                setUploading(true);
                updateUserProfile(user.uid, null, null, file, (user: User) => {
                  setUser?.({ ...user, uid: user.uid });
                  setUploading(false);
                });
              }
            }}
          />
          {uploading && (
            <div className="absolute w-full h-full flex justify-center items-center text-white-100">
              <CircularProgress color="primary" />
            </div>
          )}
          <Avatar src={user?.photo?.downloadURL || ""} size="xl" rounded="sm" />
        </div>
        <div className="flex flex-col pl-5">
          <div className="flex text-lg font-bold text-black mt-6">


            {!isEditing && <span className="text-lg font-bold text-black">{user?.displayName ? user?.displayName : "Anonymous"}</span>}
            {isEditing && (
              <TextInput
                disabled={isEditing ? false : true}
                onChange={(val) => {
                  setInputName(val);
                }}
                defaultValue={user?.displayName}
                placeholder={"Click to Add Name!"}
              />
            )}


          </div>
          <div className="flex text-sm font-light text-black">
            {user?.email}
          </div>
        </div>
      </div>
      <div className="flex justify-between m-4 ml-8 mr-8">
        <div className="flex1">
          <div className="text-sm font-bold text-gray-500 mt-4 mb-2 ml-6">
            EMAIL ADDRESS
          </div>
          <div className="text-sm font-bold text-black ml-4 w-60">
            {!isEditing && <span className="h-10 flex items-center">{user?.email}</span>}
            {isEditing && (
              <TextInput
                disabled={isEditing ? false : true}
                onChange={(val) => {
                  setInputEmail(val);
                }}
                defaultValue={user?.email}

              />
            )}
          </div>
        </div>

        <div className="flex1 ml-3">
          <div className="text-sm font-bold text-gray-500 mt-4 mb-2 ml-6">
            USER ID
          </div>
          <div className="text-sm font-bold text-black m-4">{user?.uid}</div>
        </div>
      </div>

      <div className="flex justify-between m-4 ml-8 mr-8">
        <div className="mt-5">
          <Button
            theme="gray"
            size="hug"
            rounded="2xl"
            label="Edit Profile"
            onClick={() => {
              setIsEditing(true);
            }}
          />
        </div>
        <div className="mt-5 ">
          <Button
            theme="gray"
            size="hug"
            rounded="2xl"
            label="Save Profile"
            onClick={() => {
              setIsEditing(false);
              if (user)
                updateUserProfile(
                  user.uid!,
                  inputName,
                  inputEmail,
                  null,
                  (user: User) => {
                    setUser?.({ ...user, uid: user.uid });
                  }
                );
            }}
          />
        </div>
      </div>
    </div>
  );
};
